var raw = msg.payload.rss.channel[0].item[0].description[0].split(', ');

var data = {};
var keys = {
    "Temperature"       :['tmp',function(val){return parseInt(val)}],
    "Wind Direction"    :['wind_dir',function(val){return val}],
    "Wind Speed"        :['wind_speed',function(val){return parseInt(val)}],
    "Humidity"          :['hum',function(val){return parseInt(val)}],
    "Pressure"          :['pressure',function(val){return parseInt(val)}],
    "Visibility"        :['visibility',function(val){return val}]
}

for(var i in raw){
    var bits = raw[i].split(': ');
    var key = keys[bits[0]];
    if(bits.length == 2) data[key[0]] = key[1](bits[1]);
}

msg.payload = data;
return msg;

